#include<stdio.h>
#include<stdlib.h>
#include<math.h>
#include<string.h>

long int prod_13(char *begin_ptr);

int main(){
char ser[] = "";


char *ptr_first = &ser[0];
printf("The product of the first 13 numbers is %ld\n", prod_13(ptr_first));
printf("The product of the first 13 numbers is %ld\n", prod_13(++ptr_first));

long int all_prod[strlen(ser) - 13];

for(int i = 0; i < (strlen(ser) - 13); i++){
	all_prod[i] = prod_13(ptr_first);
	++ptr_first;
}

long int max = -1;
for(int i = 0; i < (strlen(ser) - 13); i++){
	if(all_prod[i] > max){
		max = all_prod[i];
	}
}

printf("The maximum was %ld", max);

}


long int prod_13(char *begin_ptr){
	long int prod = 1;
	for(int i = 0; i < 13; ++i){
		prod *= (*(begin_ptr + i) - 48);
	}
	return prod;
}
